svg.script:
let hour = document.getElementsByClassName('hour')[0];
let minute = document.getElementsByClassName('minute')[0];
let hourOffset = -60; // initialy hour arrow is on 10 o'clock.
let minuteOffset = 60; // initialy minute arrow is on 2 o'clock.
let degHour = 30;
let degMinute = 6;
let degMinuteInHour = 0.5;

function setTime() {
  let curentHour = new Date().getHours() % 12;
  let curentMinute = new Date().getMinutes();
  hour.style.transform = `rotate(${curentHour * degHour - hourOffset + curentMinute * degMinuteInHour}deg)`;
  minute.style.transform = `rotate(${curentMinute * degMinute - minuteOffset}deg)`;
}

setTime();
setInterval(setTime(), 60000);
